		public static string GetStringForPDFExport(TestPlan testPlan)
		{
			string titleHTML = GetTitle(testPlan);

			string testSuiteWithResultsHTML = GetTestTestSuiteWithResults(testPlan, "PDF", "NA");

			string signOffTableHTML = CreateSignOffTable();

			return titleHTML + testSuiteWithResultsHTML + signOffTableHTML;
		}

		public static string GetStringForHTMLExport(TestPlan testPlan, string applicationUnderTest)
		{
			string headHTML = "<head><style>" + GetCssString() + " </style></head><body> ";

			string titleHTML = GetTitle(testPlan);

			string folderName = $@"\\barcapp.org\SharedFiles\Cerca\IT_Automation\\GeneratedEccDocuments\\{applicationUnderTest}\\{testPlan.Name.Replace("/", " ")}\\Reports";
			System.IO.Directory.CreateDirectory(folderName);

			string testSuiteWithResultsHTML = GetTestTestSuiteWithResults(testPlan, "HTML", applicationUnderTest);

			return "<html>" + headHTML + "<body>" + titleHTML + testSuiteWithResultsHTML + "</body></html>";
		}



