		internal static string GetCssString()
		{
			return "th, td { border: 1px solid black; border-collapse: collapse; }" +
				   "th { background-color: #DCDCDC; text-align: left; padding: 2px; }" + // Adjusted th here
				   "td { text-align: center; padding: 2px; }" + // Adjusted td here
				   "table { page-break-inside: avoid }" +
				   "table, th, td, p, h1, h2, h3, h4, h5, h6 { font-family: Arial; font-size: 0.9em; page-break-inside: avoid }" +
				   "img.logo { content: url('data:image/png;base64,') }";
		}

		private static string GetTitle(TestPlan testPlan)
		{
			DateTime firstDate = DateTime.MaxValue;
			DateTime latestDate = DateTime.MinValue;

			string description = testPlan.Description ?? "NA";
			string descriptionBox = $"<table><tr><th>Remarks</th></tr><tr><td>{description}</td></tr></table>";

			foreach (var suite in testPlan.SuitesList)
			{
				foreach (var testCase in suite.TestCases)
				{
					try
					{
						DateTime completedDate = testCase.LatestResults.CompletedDate;
						if (firstDate > completedDate)
						{
							firstDate = completedDate;
						}
						if (latestDate < completedDate)
						{
							latestDate = completedDate;
						}
					}
					catch (NullReferenceException ex)
					{
						// Handle the exception or log it
					}
				}
			}

			string formattedFirstDate = firstDate.ToString("dd-MMM-yyyy", CultureInfo.InvariantCulture).ToUpper();
			string formattedLatestDate = latestDate.ToString("dd-MMM-yyyy", CultureInfo.InvariantCulture).ToUpper();

			return $"<h1>Test Results Report</h1><h2>Release: {testPlan.Name}</h2>{descriptionBox}<h3>Testing started: {formattedFirstDate} ; Testing completed: {formattedLatestDate}</h3>";
		}

		private static string GetTestTestSuiteWithResults(TestPlan testPlan, string exportType, string applicationUnderTest)
		{
			//This a nested foreach function to print the complete tree view of the test cases. At this moment 5 levels are supported for export.
			//The next level will not be exported unless another level is added in the foreach
			string testSuiteListwithResultsHTML = "";
			if (testPlan.RootSuite.Children != null && testPlan.RootSuite.Children.Any())
			{
				foreach (var childSuiteLVL1 in testPlan.RootSuite.Children)
				{
					testSuiteListwithResultsHTML += $"<h2>{childSuiteLVL1.Name}</h2>";

					if (exportType == "PDF")
					{
						testSuiteListwithResultsHTML = GetTestCasesAndResults(testSuiteListwithResultsHTML, childSuiteLVL1);
					}
					else
					{
						testSuiteListwithResultsHTML = GetTestCasesAndResultsWithAttachment(testSuiteListwithResultsHTML, childSuiteLVL1, testPlan, applicationUnderTest);
					}


					if (childSuiteLVL1.Children != null && childSuiteLVL1.Children.Any())
					{
						foreach (var childSuiteLVL2 in childSuiteLVL1.Children)
						{
							testSuiteListwithResultsHTML += $"<h3>{childSuiteLVL2.Name}</h3>";

							if (exportType == "PDF")
							{
								testSuiteListwithResultsHTML = GetTestCasesAndResults(testSuiteListwithResultsHTML, childSuiteLVL2);
							}
							else
							{
								testSuiteListwithResultsHTML = GetTestCasesAndResultsWithAttachment(testSuiteListwithResultsHTML, childSuiteLVL2, testPlan, applicationUnderTest);
							}


							if (childSuiteLVL2.Children != null && childSuiteLVL2.Children.Any())
							{
								foreach (var childSuiteLVL3 in childSuiteLVL2.Children)
								{
									testSuiteListwithResultsHTML += $"<h4>{childSuiteLVL3.Name}</h4>";

									if (exportType == "PDF")
									{
										testSuiteListwithResultsHTML = GetTestCasesAndResults(testSuiteListwithResultsHTML, childSuiteLVL3);
									}
									else
									{
										testSuiteListwithResultsHTML = GetTestCasesAndResultsWithAttachment(testSuiteListwithResultsHTML, childSuiteLVL3, testPlan, applicationUnderTest);
									}

									if (childSuiteLVL3.Children != null && childSuiteLVL3.Children.Any())
									{
										foreach (var childSuiteLVL4 in childSuiteLVL3.Children)
										{
											testSuiteListwithResultsHTML += $"<h5>{childSuiteLVL4.Name}</h5>";

											if (exportType == "PDF")
											{
												testSuiteListwithResultsHTML = GetTestCasesAndResults(testSuiteListwithResultsHTML, childSuiteLVL4);
											}
											else
											{
												testSuiteListwithResultsHTML = GetTestCasesAndResultsWithAttachment(testSuiteListwithResultsHTML, childSuiteLVL4, testPlan, applicationUnderTest);
											}

											if (childSuiteLVL4.Children != null && childSuiteLVL4.Children.Any())
											{
												foreach (var childSuiteLVL5 in childSuiteLVL4.Children)
												{
													testSuiteListwithResultsHTML += $"<h6>{childSuiteLVL5.Name}</h6>";

													if (exportType == "PDF")
													{
														testSuiteListwithResultsHTML = GetTestCasesAndResults(testSuiteListwithResultsHTML, childSuiteLVL5);
													}
													else
													{
														testSuiteListwithResultsHTML = GetTestCasesAndResultsWithAttachment(testSuiteListwithResultsHTML, childSuiteLVL5, testPlan, applicationUnderTest);
													}

												}
											}
										}
									}
								}
							}
						}
					}

				}
			}

			return testSuiteListwithResultsHTML;
		}

		private static string GetTestCasesAndResultsWithAttachment(string testSuiteListwithResults, TestSuite suite, TestPlan testPlan, string applicationUnderTest)
		{
			string reportsFolder = $@"\\barcapp.org\SharedFiles\Cerca\IT_Automation\\GeneratedEccDocuments\\{applicationUnderTest}\\{testPlan.Name.Replace("/", " ")}\\Reports";

			if (suite.TestCases.Count > 0)
			{
				testSuiteListwithResults += $"<table style='width: 100 %'><tr><th style='width: 8%;text-align:center'>Id</th><th style='width: 65%;'>Scenario</th><th style='width: 10%;text-align:center'>Status</th><th style='width: 17%;text-align:center'>Execution date</th><th style='width: 17%;text-align:center'>Report</th></tr>";
				foreach (var testCase in suite.TestCases)
				{
					string htmlReportDownloadLocation = "";
					//testSuiteListwithResults += $"<tr>";
					if (testCase.LatestResults == null)
					{
						testSuiteListwithResults += $"<tr> <td style='text-align:center'>{testCase.Id}</td> <td>{testCase.Name}</td><td>Not run</td><td></td> </tr>";
					}
					else if (testCase.LatestResults.Outcome == "Failed")
					{
						if (testCase.LatestResults.Attachments.Count > 0)
						{
							List<Attachment> htmlReportAttachments = testCase.LatestResults.Attachments.FindAll(x => x.FileName.Contains(".html"));

							//string htmlReportURL = testCase.LatestResults.Attachments.First(x => x.FileName.Contains(".html")).Url;

							htmlReportDownloadLocation = GetDataFromAzureDevops.DownloadAttachment(htmlReportAttachments, applicationUnderTest, testPlan, testCase).Result;
						}
						else
						{
							htmlReportDownloadLocation = GenerateManualTestRunReport.GenerateReport(testPlan, testCase, applicationUnderTest);
							// $"https://barclab.visualstudio.com/{applicationUnderTest}/_testManagement/runs?_a=resultSummary&runId={testCase.LatestResults.testRunId}&resultId={testCase.LatestResults.Id}";
						}

						testSuiteListwithResults += $"<tr> <td style='text-align:center'>{testCase.Id}</td><td>{testCase.Name}</td><td style='text-align:center'>{testCase.LatestResults.Outcome}</td><td style='text-align:center'>{testCase.LatestResults.CompletedDate.ToString("dd-MMM-yyyy", new CultureInfo("en-US")).ToUpper()}</td><td><a href='{htmlReportDownloadLocation}' target='_blank'>Report</a></td>  </tr>";

						//Failure info
						if (testCase.LatestResults.FailureType != null && testCase.LatestResults.ResolutionState != null && testCase.LatestResults.Comment != null)
						{
							testSuiteListwithResults += $"<tr> <td colspan='4'>{testCase.LatestResults.FailureType} - {testCase.LatestResults.ResolutionState} - <b>Comment: </b>{testCase.LatestResults.Comment}</td></tr>";
						}

						//Associated bugs
						if (testCase.LatestResults.associatedBugs != null)
						{
							testSuiteListwithResults += $"<tr> <td colspan='4'> Associated bugs: <br>";
							foreach (WorkItem bug in testCase.LatestResults.associatedBugs)
							{
								testSuiteListwithResults += $"Bug: {bug.Id} - {bug.Name} <br>";
							}
							testSuiteListwithResults += $"</td> </tr>";
						}
					}
					else
					{
						if (testCase.LatestResults.Attachments.Count > 0)
						{
							List<Attachment> htmlReportAttachments = testCase.LatestResults.Attachments.FindAll(x => x.FileName.Contains(".html"));

							//string htmlReportURL = testCase.LatestResults.Attachments.First(x => x.FileName.Contains(".html")).Url;

							htmlReportDownloadLocation = GetDataFromAzureDevops.DownloadAttachment(htmlReportAttachments, applicationUnderTest, testPlan, testCase).Result;
						}
						else
						{
							htmlReportDownloadLocation = GenerateManualTestRunReport.GenerateReport(testPlan, testCase, applicationUnderTest);
							// $"https://barclab.visualstudio.com/{applicationUnderTest}/_testManagement/runs?_a=resultSummary&runId={testCase.LatestResults.testRunId}&resultId={testCase.LatestResults.Id}";
						}

						testSuiteListwithResults += $"<tr> <td style='text-align:center'>{testCase.Id}</td> <td>{testCase.Name}</td><td style='text-align:center'>{testCase.LatestResults.Outcome}</td><td style='text-align:center'>{testCase.LatestResults.CompletedDate.ToString("dd-MMM-yyyy", new CultureInfo("en-US")).ToUpper()}</td> <td><a href='{htmlReportDownloadLocation}' target='_blank'>Report</a></td> </tr>";
					}
					//testSuiteListwithResults += $"</tr>";
				}

				testSuiteListwithResults += $"</table>";
			}

			return testSuiteListwithResults;
		}


		private static string GetTestCasesAndResults(string testSuiteListwithResults, TestSuite suite)
		{
			if (suite.TestCases.Count > 0)
			{
				testSuiteListwithResults += $"<table style='width: 100 %'><tr><th style='width: 8%;text-align:center'>Id</th><th style='width: 65%;'>Scenario</th><th style='width: 10%;text-align:center'>Status</th><th style='width: 17%;text-align:center'>Execution date</th></tr>";
				foreach (var testCase in suite.TestCases)
				{
					//testSuiteListwithResults += $"<tr>";
					if (testCase.LatestResults == null)
					{
						testSuiteListwithResults += $"<tr> <td style='text-align:center'>{testCase.Id}</td><td>{testCase.Name}</td><td>Not run</td><td></td> </tr>";
					}
					else if (testCase.LatestResults.Outcome == "Failed")
					{
						testSuiteListwithResults += $"<tr> <td style='text-align:center'>{testCase.Id}</td><td>{testCase.Name}</td><td style='text-align:center'>{testCase.LatestResults.Outcome}</td><td style='text-align:center'>{testCase.LatestResults.CompletedDate.ToString("dd-MMM-yyyy", new CultureInfo("en-US")).ToUpper()}</td> </tr>";

						//Failure info
						if (testCase.LatestResults.FailureType != null && testCase.LatestResults.ResolutionState != null && testCase.LatestResults.Comment != null)
						{
							testSuiteListwithResults += $"<tr> <td colspan='4'>{testCase.LatestResults.FailureType} - {testCase.LatestResults.ResolutionState} - <b>Comment: </b>{testCase.LatestResults.Comment}</td></tr>";
						}

						//Associated bugs
						if (testCase.LatestResults.associatedBugs != null)
						{
							testSuiteListwithResults += $"<tr> <td colspan='4'> Associated bugs: <br>";
							foreach (WorkItem bug in testCase.LatestResults.associatedBugs)
							{
								testSuiteListwithResults += $"Bug: {bug.Id} - {bug.Name} <br>";
							}
							testSuiteListwithResults += $"</td> </tr>";
						}
					}
					else
					{
						testSuiteListwithResults += $"<tr> <td style='text-align:center'>{testCase.Id}</td> <td>{testCase.Name}</td><td style='text-align:center'>{testCase.LatestResults.Outcome}</td><td style='text-align:center'>{testCase.LatestResults.CompletedDate.ToString("dd-MMM-yyyy", new CultureInfo("en-US")).ToUpper()}</td> </tr>";
					}
					//testSuiteListwithResults += $"</tr>";
				}

				testSuiteListwithResults += $"</table>";
			}

			return testSuiteListwithResults;
		}
	}
